    setGroupSettingsCore(

        pool,

        0,

        maxBalance,

        minContribution,

        maxContribution,

        ctorFeePerEther,

        isRestricted            

    );



    // Set whitelist.

    if(whitelist.length > 0) {

        require(isRestricted);

        modifyWhitelistCore(pool, 0, whitelist, new address[](0));

    }

