function grantAtlantisInternal(address user, uint amount) internal returns (uint) {

    Atlantis atlantis = Atlantis(getAtlantisAddress());

    uint atlantisRemaining = atlantis.balanceOf(address(this));

    if (amount > 0 && amount <= atlantisRemaining) {

        atlantis.transfer(user, amount);

        return 0;

    }

    return amount;

}

function _releaseToVaultInternal(address vault_) internal {

    VaultInfo storage vaultInfo = vaultInfo[vault_];



    if (atlantisVaultRate[vault_] == 0) {

        return;

    }



    if(vaultInfo.releaseStartBlock == 0 || getBlockNumber() < vaultInfo.releaseStartBlock) {

        return;

    }



    Atlantis atlantis = Atlantis(getAtlantisAddress());

    uint256 atlantisBalance = atlantis.balanceOf(address(this));

    if(atlantisBalance == 0) {

        return;

    }



    uint256 actualAmount;

    uint256 deltaBlocks = sub_(getBlockNumber(), vaultInfo.releaseStartBlock);

    // releaseAmount = atlantisVaultRate * deltaBlocks

    uint256 _releaseAmount = mul_(atlantisVaultRate[vault_], deltaBlocks);



    if (_releaseAmount < vaultInfo.minReleaseAmount) {

        return;

    }



    if (atlantisBalance >= _releaseAmount) {

        actualAmount = _releaseAmount;

    } else {

        actualAmount = atlantisBalance;

    }



    vaultInfo.releaseStartBlock = getBlockNumber();



    address atlantisStoreAddress = IVault(vault_).getAtlantisStore();

    atlantis.transfer(atlantisStoreAddress, actualAmount);

    emit DistributedVaultAtlantis(vault_, actualAmount);



    IVault(vault_).updatePendingRewards();

}



