function pow(uint256 base, uint256 exponent) public pure returns (uint256) {

    if (exponent == 0) {

        return 1;

    }

    else if (exponent == 1) {

        return base;

    }

    else if (base == 0 && exponent != 0) {

        return 0;

    }

    else {

        uint256 z = base;

        for (uint256 i = 1; i < exponent; i++)

            z = mul(z, base);

        return z;

    }
