    // unused function

    // cointroller.repayBorrowVerify(address(this), payer, borrower, vars.actualRepayAmount, vars.borrowerIndex);



    return (uint(Error.NO_ERROR), vars.actualRepayAmount);

function seizeInternal(address seizerToken, address liquidator, address borrower, uint seizeTokens) internal returns (uint) {

    /* Fail if seize not allowed */

    uint allowed = cointroller.seizeAllowed(address(this), seizerToken, liquidator, borrower, seizeTokens);

    if (allowed != 0) {

        return failOpaque(Error.COINTROLLER_REJECTION, FailureInfo.LIQUIDATE_SEIZE_COINTROLLER_REJECTION, allowed);

    }



    /* Fail if borrower = liquidator */

    if (borrower == liquidator) {

        return fail(Error.INVALID_ACCOUNT_PAIR, FailureInfo.LIQUIDATE_SEIZE_LIQUIDATOR_IS_BORROWER);

    }



    MathError mathErr;

    uint borrowerTokensNew;

    uint liquidatorTokensNew;



    /*

     * We calculate the new borrower and liquidator token balances, failing on underflow/overflow:

     *  borrowerTokensNew = accountTokens[borrower] - seizeTokens

     *  liquidatorTokensNew = accountTokens[liquidator] + seizeTokens

     */

    (mathErr, borrowerTokensNew) = subUInt(accountTokens[borrower], seizeTokens);

    if (mathErr != MathError.NO_ERROR) {

        return failOpaque(Error.MATH_ERROR, FailureInfo.LIQUIDATE_SEIZE_BALANCE_DECREMENT_FAILED, uint(mathErr));

    }



    (mathErr, liquidatorTokensNew) = addUInt(accountTokens[liquidator], seizeTokens);

    if (mathErr != MathError.NO_ERROR) {

        return failOpaque(Error.MATH_ERROR, FailureInfo.LIQUIDATE_SEIZE_BALANCE_INCREMENT_FAILED, uint(mathErr));

    }



    /////////////////////////

    // EFFECTS & INTERACTIONS

    // (No safe failures beyond this point)



    /* We write the previously calculated values into storage */

    accountTokens[borrower] = borrowerTokensNew;

    accountTokens[liquidator] = liquidatorTokensNew;



    /* Emit a Transfer event */

    emit Transfer(borrower, liquidator, seizeTokens);



    /* We call the defense hook */

    // unused function

    // cointroller.seizeVerify(address(this), seizerToken, liquidator, borrower, seizeTokens);



    return uint(Error.NO_ERROR);

function transferTokens(address spender, address src, address dst, uint tokens) internal returns (uint) {

    /* Fail if transfer not allowed */

    uint allowed = cointroller.transferAllowed(address(this), src, dst, tokens);

    if (allowed != 0) {

        return failOpaque(Error.COINTROLLER_REJECTION, FailureInfo.TRANSFER_COINTROLLER_REJECTION, allowed);

    }



    /* Do not allow self-transfers */

    if (src == dst) {

        return fail(Error.BAD_INPUT, FailureInfo.TRANSFER_NOT_ALLOWED);

    }



    /* Get the allowance, infinite for the account owner */

    uint startingAllowance = 0;

    if (spender == src) {

        startingAllowance = uint(-1);

    } else {

        startingAllowance = transferAllowances[src][spender];

    }



    /* Do the calculations, checking for {under,over}flow */

    MathError mathErr;

    uint allowanceNew;

    uint srrTokensNew;

    uint dstTokensNew;



    (mathErr, allowanceNew) = subUInt(startingAllowance, tokens);

    if (mathErr != MathError.NO_ERROR) {

        return fail(Error.MATH_ERROR, FailureInfo.TRANSFER_NOT_ALLOWED);

    }



    (mathErr, srrTokensNew) = subUInt(accountTokens[src], tokens);

    if (mathErr != MathError.NO_ERROR) {

        return fail(Error.MATH_ERROR, FailureInfo.TRANSFER_NOT_ENOUGH);

    }



    (mathErr, dstTokensNew) = addUInt(accountTokens[dst], tokens);

    if (mathErr != MathError.NO_ERROR) {

        return fail(Error.MATH_ERROR, FailureInfo.TRANSFER_TOO_MUCH);

    }



    /////////////////////////

    // EFFECTS & INTERACTIONS

    // (No safe failures beyond this point)



    accountTokens[src] = srrTokensNew;

    accountTokens[dst] = dstTokensNew;



    /* Eat some of the allowance (if necessary) */

    if (startingAllowance != uint(-1)) {

        transferAllowances[src][spender] = allowanceNew;

    }



    /* We emit a Transfer event */

    emit Transfer(src, dst, tokens);



    // unused function

    // cointroller.transferVerify(address(this), src, dst, tokens);



    return uint(Error.NO_ERROR);

function doTransferIn(address from, uint amount) internal returns (uint) {

    EIP20NonStandardInterface token = EIP20NonStandardInterface(underlying);

    uint balanceBefore = EIP20Interface(underlying).balanceOf(address(this));

    token.transferFrom(from, address(this), amount);



    bool success;

    assembly {

        switch returndatasize()

            case 0 {                       // This is a non-standard BEP-20

                success := not(0)          // set success to true

            }

            case 32 {                      // This is a compliant BEP-20

                returndatacopy(0, 0, 32)

                success := mload(0)        // Set `success = returndata` of external call

            }

            default {                      // This is an excessively non-compliant BEP-20, revert.

                revert(0, 0)

            }

    }

    require(success, "TOKEN_TRANSFER_IN_FAILED");



    // Calculate the amount that was *actually* transferred

    uint balanceAfter = EIP20Interface(underlying).balanceOf(address(this));

    require(balanceAfter >= balanceBefore, "TOKEN_TRANSFER_IN_OVERFLOW");

    return balanceAfter - balanceBefore;   // underflow already checked above, just subtract

}

    function doTransferOut(address payable to, uint amount) internal {

        EIP20NonStandardInterface token = EIP20NonStandardInterface(underlying);

        token.transfer(to, amount);



        bool success;

        assembly {

            switch returndatasize()

                case 0 {                      // This is a non-standard BEP-20

                    success := not(0)          // set success to true

                }

                case 32 {                     // This is a complaint BEP-20

                    returndatacopy(0, 0, 32)

                    success := mload(0)        // Set `success = returndata` of external call

                }

                default {                     // This is an excessively non-compliant BEP-20, revert.

                    revert(0, 0)

                }

        }

        require(success, "TOKEN_TRANSFER_OUT_FAILED");

    }

}

function delegateTo(address callee, bytes memory data) internal returns (bytes memory) {

    (bool success, bytes memory returnData) = callee.delegatecall(data);

    assembly {

        if eq(success, 0) {

            revert(add(returnData, 0x20), returndatasize)

        }

    }

    return returnData;

}

function delegateToViewImplementation(bytes memory data) public view returns (bytes memory) {

    (bool success, bytes memory returnData) = address(this).staticcall(abi.encodeWithSignature("delegateToImplementation(bytes)", data));

    assembly {

        if eq(success, 0) {

            revert(add(returnData, 0x20), returndatasize)

        }

    }

    return abi.decode(returnData, (bytes));

}

    function () external payable {

        require(msg.value == 0,"RBep20Delegator:fallback: cannot send value to fallback");



        // delegate all other functions to current implementation

        (bool success, ) = implementation.delegatecall(msg.data);



        assembly {

            let free_mem_ptr := mload(0x40)

            returndatacopy(free_mem_ptr, 0, returndatasize)



            switch success

            case 0 { revert(free_mem_ptr, returndatasize) }

            default { return(free_mem_ptr, returndatasize) }

        }

    }

}

    function () payable external {

        // delegate all other functions to current implementation

        (bool success, ) = cointrollerImplementation.delegatecall(msg.data);



        assembly {

              let free_mem_ptr := mload(0x40)

              returndatacopy(free_mem_ptr, 0, returndatasize)



              switch success

              case 0 { revert(free_mem_ptr, returndatasize) }

              default { return(free_mem_ptr, returndatasize) }

        }

    }

}

