function scriptNumEncode(uint256 num) public pure returns (bytes memory) {

    uint a = scriptNumSize(num);

    bytes memory b = toBytes(num);

    for (uint i = 0; i < (a/2); i++) {

        byte c = b[i];

        b[i] = b[a - i - 1];

        b[a - i - 1] = c;

    }

    return b;
