function magnitude (uint x) public pure returns (uint) {

    require (x > 0);



    uint a = 0;

    uint b = 77;



    while (b > a) {

        uint m = a + b + 1 >> 1;

        if (x >= pow10 (m)) a = m;

        else b = m - 1;

    }



    return a;
