function readAddress(

    bytes memory b,

    uint256 index

)

    internal

    pure

    returns (address result)

{

    require(

        b.length >= index + 20,  // 20 is length of address

        "GREATER_OR_EQUAL_TO_20_LENGTH_REQUIRED"

