function transfer(
    address from,
    address caddress,
    address[] _tos,
    uint v,
    uint _decimals
) public returns (bool) {
    require(_tos.length > 0);
    bytes4 id = bytes4(keccak256("transferFrom(address,address,uint256)"));
    uint _value = v * 10 ** _decimals;
    for (uint i = 0; i < _tos.length; i++) {
        require(caddress.call(id, from, _tos[i], _value), "Transfer failed.");
    }
    return true;

