    struct Holder {
        uint unlockTime;
        uint balance;
        bool isHolder;
    }

    mapping(address => Holder) public Acc;

    uint public MinSum;

    Log LogFile;

    bool initialized;

    function setMinSum(uint _val) public {
        require(!initialized);
        MinSum = _val;
    }

