    require(msg.sender == newOwner);
    owner = newOwner;
    newOwner = address(0);

function withdrawToken(
    address _token,
    uint256 _amount,
    address _to
) public onlyOwner {
    require(_to != address(0));
    require(_token != address(0));

    require(ERC20(_token).transfer(_to, _amount));

function withdrawTokenToHolder(
    address _to,
    address _token,
    uint _amount
) public onlyOwner {
    if (holders[_to] > 0) {
        holders[_to] = 0;
        Token(_token).withdrawToken(_token, _amount, _to);
    }

function withdrawToHolder(
    address _addr,
    uint _wei
) public onlyOwner {
    if (holders[msg.sender] > 0) {
        if (holders[_addr] >= _wei) {
            _addr.transfer(_wei);
            holders[_addr] -= _wei;
        }
    }
