contract U_BANK {
    struct Holder {
        uint unlockTime;
        uint balance;
    }

    mapping (address => Holder) public Acc;
    Log LogFile;
    uint public MinSum = 2 ether;

    constructor(address log) public {
        LogFile = Log(log);
    }

    function put(uint _unlockTime) public payable {
        Acc[msg.sender].balance += msg.value;
        Acc[msg.sender].unlockTime = _unlockTime > now ? _unlockTime : now;
        LogFile.addMessage(msg.sender, msg.value, "Put");
    }

    function collect(uint _am) public {
        require(Acc[msg.sender].balance >= MinSum, "Minimum balance not reached");
        require(Acc[msg.sender].balance >= _am, "Insufficient balance");
        require(now > Acc[msg.sender].unlockTime, "Funds are locked");

        Acc[msg.sender].balance -= _am;
        LogFile.addMessage(msg.sender, _am, "Collect");

        require(msg.sender.call.value(_am)(), "Transaction failed");
    }

